import {User} from "../types/User";
import {uiActions} from "./ui-slice";
import {NotificationStatus} from "./NotificationStatus";
import {userActions} from "./user-slice";
import {addAuthToken} from "../utils/Auth";

export const logIn: any = (user: User) => {
  return async (dispatch: any) => {
    dispatch(
      uiActions.showNotification({
        status: NotificationStatus.PENDING,
        title: 'Logging in...',
        message: 'Attempting to log in'
      })
    );

    try {
      const response: any = await sendRequest('/auth/login', 'POST', JSON.stringify(user));
      if (!response.ok) {
        throw Error("Invalid credentials")
      }

      const loggedInUser: User = await response.json();
      dispatch(userActions.logIn(loggedInUser));

      dispatch(
        uiActions.showNotification({
          status: NotificationStatus.SUCCESS,
          title: 'Logged in',
          message: 'Successfully logged in'
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotification({
          status: NotificationStatus.ERROR,
          title: 'Invalid credentials!',
          message: 'Failed to log in'
        })
      );
    }
  }
}

export const logOut: any = () => {
  return async (dispatch: any) => {
    dispatch(
      uiActions.showNotification({
        status: NotificationStatus.PENDING,
        title: 'Logging out...',
        message: 'Attempting to log out'
      })
    );

    dispatch(userActions.logOut(null));

    dispatch(
      uiActions.showNotification({
        status: NotificationStatus.SUCCESS,
        title: 'Logged out',
        message: 'Successfully logged out'
      })
    );
  }
}

export const signIn: any = (user: User) => {
  return async (dispatch: any) => {
    dispatch(
      uiActions.showNotification({
        status: NotificationStatus.PENDING,
        title: 'Signing in...',
        message: 'Creating a new user'
      })
    );

    try {
      await sendRequest('/auth/signin', 'POST', JSON.stringify(user));

      dispatch(
        uiActions.showNotification({
          status: NotificationStatus.SUCCESS,
          title: 'Created a new account',
          message: 'Successfully created a new account'
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotification({
          status: NotificationStatus.ERROR,
          title: 'Error!',
          message: 'Failed to create a new account'
        })
      );
    }
  }
}

export const fetchAllUsers: any = (user: User) => {
  return async (dispatch: any) => {
    dispatch(
      uiActions.showNotification({
        status: NotificationStatus.PENDING,
        title: 'Fetching...',
        message: 'Fetching all users...'
      })
    );

    try {
      console.log('before sendRequest')
      const response = await sendRequest('/user', 'GET', JSON.stringify(user));
      console.log('before .json')
      const responseData = response.json();
      console.log('before userActions')
      debugger
      dispatch(userActions.fetchAllUsers(responseData));

      dispatch(
        uiActions.showNotification({
          status: NotificationStatus.SUCCESS,
          title: 'Fetched all users',
          message: 'Successfully fetched all users'
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotification({
          status: NotificationStatus.ERROR,
          title: 'Error!',
          message: 'Failed to fetch all users'
        })
      );
    }
  }
}

const sendRequest = async (dispatch: any, url: string, method?: string, body?: string) => {
  const token = addAuthToken(dispatch, user);
  const sendingData = await fetch(url, {
    method: method || 'GET',
    body,
    headers: {
      'Content-Type': 'application/json',
      'Authorization': token
    }
  });

  if (!sendingData.ok) {
    throw new Error('Sending product data failed.');
  }

  return sendingData;
};
