import {Product} from "../../../types/Product";
import {useState} from "react";
import classes from './AddProduct.module.css';
import {useDispatch, useSelector} from "react-redux";
import {addNewProduct} from "../../../store/product-actions";

const AddProduct = () => {
  const dispatch = useDispatch();
  const user = useSelector((state: any) => state.user.user);
  const [nameCssClass, setNameCssClass] = useState('');
  const [priceCssClass, setPriceCssClass] = useState('');
  const [quantityCssClass, setQuantityCssClass] = useState('');
  const [isValid, setIsValid] = useState(false);

  const [product, setProduct] = useState<Product>({
    name: "",
    quantity: 0,
    price: 0
  });

  const onChangeNameHandler = (event: any) => {
    event.preventDefault();
    const value = event.target.value
    setProduct({...product, name: value});
    if (!isNameValid) {
      setNameCssClass(classes.invalid);
    } else {
      setNameCssClass(classes.valid);
    }
  }

  const onChangePriceHandler = (event: any) => {
    let value: number = event.target.value;//parseFloat(event.target.value);
    console.log(value)
    value = isNaN(value) ? 0 : value;
    setProduct({...product, price: value});

    if (!value || !isNumberValid(value)) {
      setPriceCssClass(classes.invalid);
    } else {
      setPriceCssClass(classes.valid);
    }
  }

  const onChangeQuantityHandler = (event: any) => {
    console.log(event.target.value)
    let value: number = parseInt(event.target.value, 10);
    debugger
    value = isNaN(value) ? 0 : value;
    setProduct({...product, quantity: value});
console.log(product.quantity)
    if (!value || !isNumberValid(value)) {
      setQuantityCssClass(classes.invalid);
    } else {
      setQuantityCssClass(classes.valid);
    }
  }

  const onAddProductHandler = (event: any) => {
    event.preventDefault();
    dispatch(addNewProduct(product, user));
    setProduct({
      productId: 0,
      name: '',
      quantity: 0,
      price: 0
    });
  }

  const validateProduct = () => {
    console.log('validate called: isValid: ' + isValid)
    setIsValid(isNameValid() && isNumberValid(product.price) && isNumberValid(product.quantity));
  };

  const isNameValid = () => {
    return product.name.trim().length > 2;
  }

  const isNumberValid = (num: number) => {
    return num > 0;
  }
//onChange={validateProduct}
  return (
    <div>
      <form onSubmit={onAddProductHandler} >
        <div>
          <label>Name:</label>
          <input className={`form-control ${isNameValid() ? '' : 'invalid'}`} type="text" id="name" value={product.name} onChange={onChangeNameHandler} placeholder="Name"/>
        </div>
        <div>
          <label>Price:</label>
          <input className="form-control" type="number" step="0.01" id="price" value={product.price} onChange={onChangePriceHandler} placeholder="0"/>
        </div>
        <div>
          <label>Quantity:</label>
          <input className="form-control" type="number" id="quantity" value={product.quantity} onChange={onChangeQuantityHandler} placeholder="0"/>
        </div>
        <button disabled={!isValid} type="submit" className="btn btn-primary" >Add new product </button>
      </form>
    </div>
  )
}

export default AddProduct;
