import {useState} from "react";
import {User, UserLevel} from "../../types/User";
import {useDispatch, useSelector} from "react-redux";
import {userActions} from "../../store/user-slice";
import {deleteUser} from "../../store/user-actions";

const SingleUser = (props: any) => {
  const initialUsername: string = props.username;
  const initialUserLevel: UserLevel = props.userLevel;
  const [isValid, setIsValid] = useState(false);
  const [user, setUser] = useState<User>({
    password: "",
    token: "",
    userId: 0,
    userLevel: undefined,
    username: ""
  });

  const dispatch = useDispatch();
  const activeUser: User = useSelector((state: any) => state.user.user);

  const onUsernameChangeHandler = (event: any) => {
    const value = event.target.value;

    if (value.length > 2) {
      setUser({...user, username: value});
    } else {
      setUser({...user, username: initialUsername});
    }
  }

  const onUserLevelChangeHandler = (event: any) => {
    const value = event.target.value;

    setUser({...user, userLevel: value});
    // if (value.length > 2) {
    // } else {
    //   setUser({...user, userLevel: UserLevel.USER});
    // }
  }

  const onUserUpdateHandler = (event: any) => {
    // const ul: UserLevel = userLevel;
    // const updated: Product = {username, ul} as Product;
    console.log("Updated!")
  }

  const onUserDeleteHandler = () => {
    dispatch(deleteUser(user, activeUser));
    console.log("DELETE!")
  }

  return (
    <li className='row' style={{margin: "1rem"}}>
      <form onSubmit={onUserUpdateHandler}>
        <input type="text" className="col-xl cell-area" onChange={onUsernameChangeHandler} defaultValue={user.username}/>
        <input type="text" className="col-xl cell-area" onChange={onUserLevelChangeHandler} value={user.userLevel}/>
        <button className="col-sm btn btn-primary" type="button" onClick={onUserUpdateHandler}>Update</button>
        <button className="col-sm btn btn-danger" type="button" onClick={onUserDeleteHandler}>Delete</button>
      </form>
    </li>
  );
}

export default SingleUser;
