import React, {useEffect} from "react";
import ProductList from "./ProductList/ProductList";
import {fetchProductsData} from "../../store/product-actions";
import {useDispatch, useSelector} from "react-redux";
import AddProduct from "./AddProduct/AddProduct";
import classes from "./Products.module.css";

const Products: React.FC = () => {
  const productsState = useSelector((state: any) => state.product);
  const user = useSelector((state: any) => state.user.user);
  const dispatch = useDispatch();

  useEffect(() => {
    if (user) {
      dispatch(fetchProductsData(user));
    }
  }, [dispatch, user]);

  const totalPriceClasses = `rounded btn ${classes.total}`;
  console.log(totalPriceClasses)
  return (
    <>
      { productsState.products.length > 0
      ? (<>
          <h1>List of all products:</h1>
          <ProductList products={productsState.products} />
          <div className={classes.total}>Total Price: {productsState.totalPrice.toFixed(2)}</div>
        </>)
      : (<AddProduct/>)
      }
    </>
  )
}

export default Products;
