import React, {useEffect, useState} from 'react';
import './App.css';
import Navigation from "./component/Navigation/Navigation";
import Products from "./component/Products/Products";
import Footer from "./component/Footer/Footer";
import AddProduct from "./component/Products/AddProduct/AddProduct";
import {Route} from "react-router-dom";
import Welcome from "./component/Welcome/Welcome";
import Notification from "./component/Notification/Notification";
import {useSelector} from "react-redux";
import Print from "./component/Print/Print";
import Login from "./component/Login/Login";
import GuardedRoute from "./utils/GuardedRoute";

function App() {

  const notification = useSelector((state: any) => state.ui.notification);
  const user = useSelector((state: any) => state.user.user);
  console.log('user app: ', user )

  const protectedRoute = (path: string, component: JSX.Element) => {
    return <>
      {user
        ? <Route path={path}>{component}</Route>
        : <Route path="/login"><Login/></Route>
      }
      </>;
  }

  return (
    <div className="App">
      <Navigation/>
      <div className="container">
        {notification &&
          <Notification status={notification.status} title={notification.title} message={notification.message}/>}
        <Route path="/" exact><Welcome/></Route>
        {/*<Route path="/list"><Products/></Route>*/}
        {/*<Route path="/add"><AddProduct/></Route>*/}
        {protectedRoute("/list", <Products/>)}
        {protectedRoute("/add", <AddProduct/>)}
        {protectedRoute("/print", <Print/>)}
        <Route path="/login"><Login/></Route>
        {/*<GuardedRoute component={<Products />} '/list'}/>*/}
        {/*<GuardedRoute path='/add' component={AddProduct} auth {...user} />*/}
        {/*<GuardedRoute path='/print' component={Print} auth {...user} />*/}
      </div>
      <Footer/>
    </div>
  );
}

export default App;
