import {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {User} from "../../types/User";
import {logIn, signIn} from "../../store/user-actions";
import {useHistory, useLocation} from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isValid, setIsValid] = useState(false);

  const user: User = useSelector((state: any) => state.user.user);
  const dispatch = useDispatch();
  const history = useHistory();
  const location = useLocation();

  const onUsernameChangeHandler = (event: any) => {
    event.preventDefault();
    const value = event.target.value;

    value.length < 2 ? setUsername('') : setUsername(value);
    validateUserData();
  }

  const onPasswordChangeHandler = (event: any) => {
    event.preventDefault();
    const value = event.target.value;

    value.length < 2 ? setPassword('') : setPassword(value);
    validateUserData();
  }

  const onLogInClick = async (event: any) => {
    event.preventDefault();
    if (isValid) {
      const user: User = {username, password};
      await dispatch(logIn(user))
      history.replace(location?.state || '/list');
    }
  }

  const onSignInClick = async (event: any) => {
    event.preventDefault();
    if (isValid) {
      const user: User = {username, password};
      await dispatch(signIn(user))
      history.replace(location?.state || '/');
    }
  }

  const validateUserData = () => {
    setIsValid(username.length > 0 && password.length > 0);
  }

  return (
    <div>
      <form>
        <div>
          <label>Username:</label>
          <input type='text' className="form-control" onChange={onUsernameChangeHandler} placeholder="Username"/>
        </div>
        <div>
          <label>Password:</label>
          <input type='password' className="form-control" onChange={onPasswordChangeHandler} placeholder="Password"/>
        </div>
        <button type="submit" className="btn btn-primary" disabled={!isValid} onClick={onLogInClick}>Log in</button>
        {!user && <button type="submit" className="btn btn-primary" disabled={!isValid} onClick={onSignInClick}>Sign in</button>}
      </form>
    </div>
  );
}

export default Login;
