import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {fetchAllUsers} from "../../store/user-actions";
import {User, UserLevel} from "../../types/User";
import AdminList from "./AdminList";

const AdminBoard = () => {
  const user = useSelector((state: any) => state.user.user);
  const dispatch = useDispatch();

  useEffect(() => {
    if (user && user.userLevel === UserLevel.ADMIN) {
      dispatch(fetchAllUsers(user));
    }
  }, [dispatch, user]);

  const onUserDeleteHandler = () => {
    console.log("DELETE!")
  }

  console.log(user.users)
  const users: Array<User> = user.users;
  const usersList = users.map((u: User) =>
    <>
      <div>u.username</div>
      <div>u.userLevel</div>
      <button type="button" onClick={onUserDeleteHandler}>Delete user</button>
    </>
  )

  return (
    <>
      <div>Welcome to Admin Board</div>
      <AdminList users={users} />
      <ul>
        {usersList}
      </ul>
    </>
  )

}

export default AdminBoard;
