import {Product} from "../../../types/Product";
import ProductItem from "../ProductItem/ProductItem";
import React, {useState} from "react";
import {useSelector} from "react-redux";
import Search from "../../Search/Search";

const ProductList = (props: any) => {
  const user = useSelector((state: any) => state.user.user);
  const { products } = props;
  const [searchText, setSearchText] = useState('');

  const productList = products.map((product: Product) =>
    <ProductItem
      key={product.productId}
      productId={product.productId}
      name={product.name}
      price={product.price}
      quantity={product.quantity}
      user={user}
    />
  );

  let filteredList;

  const onFilterHandler = (event: any) => {
    const enteredValue: string = event.target.value.toLowerCase();
    setSearchText(enteredValue);
    debugger

    if (!enteredValue) {
      return;
    }

    filteredList = products
      .filter((product: Product) => product.name?.toLowerCase().includes(enteredValue))
      .map((product: Product) =>
        <ProductItem
          key={product.productId}
          productId={product.productId}
          name={product.name}
          price={product.price}
          quantity={product.quantity}
          user={user}
        />
      );
  }

  console.log((searchText && filteredList))
  console.log(productList)
  return (
    <div>
      <div className="row">
        <Search filter={onFilterHandler}/>
        <hr />
        <ul>
          { (searchText && filteredList) || productList }
        </ul>
        <hr />
      </div>
    </div>
  )
}

export default ProductList;
