import {useState} from "react";
import {useDispatch} from "react-redux";
import {User} from "../../types/User";
import {logIn} from "../../store/user-actions";

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();

  const onUsernameChangeHandler = (event: any) => {
    event.preventDefault();
    setUsername(event.target.value);
  }

  const onPasswordChangeHandler = (event: any) => {
    event.preventDefault();
    setPassword(event.target.value);
  }

  const onSubmit = (event: any) => {
    event.preventDefault();
    const user: User = {username, password};
    dispatch(logIn(user))
  }

  return (
    <div>
      <form>
        <div>
          <label>Username:</label>
          <input type='text' onChange={onUsernameChangeHandler} placeholder="Username"/>
        </div>
        <div>
          <label>Password:</label>
          <input type='password' onChange={onPasswordChangeHandler} placeholder="Password"/>
        </div>
        <button type="submit" onClick={onSubmit}>Log in</button>
      </form>
    </div>
  );
}

export default Login;
