import {Product} from "../../../types/Product";
import ProductItem from "../ProductItem/ProductItem";
import React, {useState} from "react";
import {useSelector} from "react-redux";
import Search from "../../Search/Search";

const ProductList = (props: any) => {
  const user = useSelector((state: any) => state.user.user);
  const [searchText, setSearchText] = useState('');
  const { products } = props;

  const getProductList = () => {
    return products.map((product: Product) =>
      <ProductItem
        key={product.productId}
        productId={product.productId}
        name={product.name}
        price={product.price}
        quantity={product.quantity}
        user={user}
      />
    );
  }

  let productList = getProductList();

  const onFilterHandlerParent = (event: any) => {
    const enteredValue: string = event.target.value;
    setSearchText(enteredValue);

    if (!searchText) {
      return;
    }
    console.log(searchText);

    searchText
    ? productList
      .filter((product: Product) => {console.log(product?.name); return product.name?.toLowerCase().includes(searchText.toLowerCase())})
      .map((product: Product) =>
        <ProductItem
          key={product.productId}
          productId={product.productId}
          name={product.name}
          price={product.price}
          quantity={product.quantity}
          user={user}
        />
    )
    : getProductList();
  }

  return (
    <div>
      <div className="row">
        <div>List of products:</div>
        <Search filter={onFilterHandlerParent}/>
        <hr />
        <ul>
          { productList }
        </ul>
        <hr />
      </div>
    </div>
  )
}

export default ProductList;
