import {useState} from "react";
import {useDispatch} from "react-redux";
import {User} from "../../types/User";
import {logIn} from "../../store/user-actions";

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isValid, setIsValid] = useState(false);

  const dispatch = useDispatch();

  const onUsernameChangeHandler = (event: any) => {
    event.preventDefault();
    const value = event.target.value;
    console.log(value)
    console.log(username)
    console.log('\n\n')
    if (value.length < 2) {
      console.log('validation for username failed')
      setUsername('');
    } else {
      setUsername(value);
    }
    validateUserData();
  }

  const onPasswordChangeHandler = (event: any) => {
    event.preventDefault();
    const value = event.target.value;
    console.log(value)
    console.log(password)
    console.log('\n\n')
    if (value.length < 2) {
      console.log('validation for password failed')
      setPassword('');
    } else {
      setPassword(value);
    }
    validateUserData();
  }

  const onSubmit = (event: any) => {
    event.preventDefault();
    if (isValid) {
      const user: User = {username, password};
      dispatch(logIn(user))
    }
  }

  const validateUserData = () => {
    setIsValid(username.length > 0 && password.length > 0);
  }

  return (
    <div>
      <form onClick={onSubmit}>
        <div>
          <label>Username:</label>
          <input type='text' className="form-control" onChange={onUsernameChangeHandler} placeholder="Username"/>
        </div>
        <div>
          <label>Password:</label>
          <input type='password' className="form-control" onChange={onPasswordChangeHandler} placeholder="Password"/>
        </div>
        <button type="submit" className="btn btn-primary" disabled={!isValid}>Log in</button>
      </form>
    </div>
  );
}

export default Login;
