import React, {useEffect} from "react";
import ProductList from "./ProductList/ProductList";
import {fetchProductsData} from "../../store/product-actions";
import {useDispatch, useSelector} from "react-redux";
import AddProduct from "./AddProduct/AddProduct";

const Products: React.FC = () => {
  const productsState = useSelector((state: any) => state.product);
  const user = useSelector((state: any) => state.user.user);
  const dispatch = useDispatch();

  useEffect(() => {
    if (user) {
      dispatch(fetchProductsData(user));
    }
  }, [dispatch, user]);

  return (
    <>
      <h1>List of all products:</h1>
      { productsState.products.length > 0 && <ProductList products={productsState.products} /> }
      { productsState.products.length === 0 && <AddProduct/> }
      <div>Total Price: {productsState.totalPrice.toFixed(2)}</div>
    </>
  )
}

export default Products;
