import {User} from "../types/User";
import {uiActions} from "./ui-slice";
import {NotificationStatus} from "./NotificationStatus";

export const logIn: any = (user: User) => {
  return async (dispatch: any) => {
    dispatch(
      uiActions.showNotification({
        status: NotificationStatus.PENDING,
        title: 'Logging in...',
        message: 'Attempting to log in'
      })
    );

    try {
      const response = await sendRequest('/logIn', 'POST', JSON.stringify(user));
      const responseData = await response.json();
      console.log(responseData);

      dispatch(
        uiActions.showNotification({
          status: NotificationStatus.SUCCESS,
          title: 'Logged in',
          message: 'Successfully logged in'
        })
      );
    } catch (error) {
      dispatch(
        uiActions.showNotification({
          status: NotificationStatus.ERROR,
          title: 'Invalid credentials!',
          message: 'Failed to log in'
        })
      );
    }
  }
}

const sendRequest = async (url: string, method?: string, body?: string) => {
  const sendingData = await fetch(url, {
    method: method || 'GET',
    body,
    headers: {
      'Content-Type': 'application/json',
    }
  });

  if (!sendingData.ok) {
    throw new Error('Sending product data failed.');
  }

  return sendingData;
};
