import {useState} from "react";

const SingleUser = (props: any) => {
  const [username, setUsername] = useState(props.username);
  const [userLevel, setUserLevel] = useState(props.userLevel);


  const onUserChangeHandler = () => {
    console.log("CHANGE!")
  }

  const onUserDeleteHandler = () => {
    console.log("DELETE!")
  }

  return (
    <li className='row' style={{margin: "1rem"}}>
      <form onSubmit={onUserChangeHandler}>
        <input type="text" className="col-xl cell-area">{username}</input>
        <input type="text" className="col-xl cell-area">{userLevel}</input>
        <button className="col-sm btn btn-primary" type="button" onClick={onUserChangeHandler}>Update</button>
        <button className="col-sm btn btn-danger" type="button" onClick={onUserDeleteHandler}>Delete</button>
      </form>
    </li>
  );
}

export default SingleUser;
