const GuardedRoute = (component: React.Component, user: User, rest: any) => {
  return (
    <Route {...rest}>
      {user
        ? <Component {...props} />
        : <Redirect to='/login'/>}
    )}</Route>
  );
}

export default GuardedRoute;
