import {Product} from "../../../types/Product";
import ProductItem from "../ProductItem/ProductItem";
import React, {useState} from "react";
import {useSelector} from "react-redux";
import Search from "../../Search/Search";

const ProductList = (props: any) => {
  const user = useSelector((state: any) => state.user.user);
  const { products } = props;
  let productList: any;

  const getProductList = () => {
    return products.map((product: Product) =>
      <ProductItem
        key={product.productId}
        productId={product.productId}
        name={product.name}
        price={product.price}
        quantity={product.quantity}
        user={user}
      />
    );
  }

  const allProducts = products.map((product: Product) =>
    <ProductItem
      key={product.productId}
      productId={product.productId}
      name={product.name}
      price={product.price}
      quantity={product.quantity}
      user={user}
    />
  );

  const onFilterHandlerParent = (event: any) => {
    const enteredValue: string = event.target.value;

    if (!enteredValue) {
      return;
    }

    const filtered = products
      .filter((product: Product) => {console.log(product?.name); return product.name?.toLowerCase().includes(enteredValue.toLowerCase())})
      .map((product: Product) =>
        <ProductItem
          key={product.productId}
          productId={product.productId}
          name={product.name}
          price={product.price}
          quantity={product.quantity}
          user={user}
        />
      );

    productList = enteredValue ? filtered : allProducts;
  }

  return (
    <div>
      <div className="row">
        <div>List of products:</div>
        <Search filter={onFilterHandlerParent}/>
        <hr />
        <ul>
          { productList }
        </ul>
        <hr />
      </div>
    </div>
  )
}

export default ProductList;
