import {createSlice} from "@reduxjs/toolkit";
import {User} from "../types/User";

export type InitialUserState = {
  user: User | null
}

const initialState = {
  user: null
} as InitialUserState;

const userSlice = createSlice({
  name: 'user',
  initialState: initialState,
  reducers: {
    logIn(state, action) {
      console.log(action)
      debugger
      const user = action.payload.user as User;
      console.log('user slice --- user LOGIN: ' + user)
      state.user = user
    },
    logOut(state, action) {
      console.log('user slice --- user logout: ' + state.user)
      state.user = null
      console.log('user slice --- user logout end: ' + state.user)
    },
    signIn(state, action) {

    }
  }
});

export const userActions = userSlice.actions;

export default userSlice;
